// BubbleSortVisualizer.js
import React, { useState, useEffect } from 'react';
import './BubbleSortVisualizer.css';

export default function BubbleSortVisualizer() {
  const [array, setArray] = useState([]);
  const [sorting, setSorting] = useState(false);

  // Generate a new random array
  const generateArray = () => {
    const newArray = Array.from({ length: 10 }, () => Math.floor(Math.random() * 100) + 1);
    setArray(newArray);
    setSorting(false);
  };

  // Bubble Sort algorithm with visual effect
  const bubbleSort = async () => {
    setSorting(true);
    let arr = [...array];
    for (let i = 0; i < arr.length; i++) {
      for (let j = 0; j < arr.length - i - 1; j++) {
        if (arr[j] > arr[j + 1]) {
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
          setArray([...arr]); // Update array to reflect the swap visually
          await new Promise((resolve) => setTimeout(resolve, 100)); // Delay for animation
        }
      }
    }
    setSorting(false);
  };

  // Initial array setup
  useEffect(() => {
    generateArray();
  }, []);

  return (
    <div className="bubble-sort-visualizer">
      <h2>Bubble Sort Visualization</h2>
      <div className="array-container">
        {array.map((value, idx) => (
          <div
            key={idx}
            className="array-bar"
            style={{ height: `${value * 3}px` }}
          ></div>
        ))}
      </div>
      <div className="controls">
        <button onClick={generateArray} disabled={sorting}>
          Generate New Array
        </button>
        <button onClick={bubbleSort} disabled={sorting}>
          Start Bubble Sort
        </button>
      </div>
    </div>
  );
}
